import React, { useState, useEffect, useContext, useReducer, useCallback, useMemo } from "react";
import axios from "axios";

//import "bootstrap/dist/css/bootstrap.min.css";
//import "../static/site.css";

import { ConfigContext } from "./App";
import { Header } from "../src/Header";
import { Menu } from "../src/Menu";
import SpeakerData from "./SpeakerData";
import SpeakerDetail from "./SpeakerDetail";
import speakersReducer from "./speakersReducer";
import useAxiosFetch from "./useAxiosFetch";

const Speakers = ({ }) => {

  //const { data, isLoading, hasErrored, errorMessage, updateDataRecord
  //} = useAxiosFetch("http://172.16.0.0:3050/api/speakers", []);

  //const { data, isLoading, hasErrored, errorMessage, updateDataRecord
  // } = useAxiosFetch("http://172.16.0.0:4000/speakers", []);

   const { data, isLoading, hasErrored, errorMessage, updateDataRecord
   } = useAxiosFetch("http://localhost:3050/api/speakers", []);

  const [speakingSaturday, setSpeakingSaturday] = useState(true);
  const [speakingSunday, setSpeakingSunday] = useState(true);
  //const context = useContext(ConfigContext);

  const handleChangeSaturday = () => {
    setSpeakingSaturday(!speakingSaturday);
  };
  const handleChangeSunday = () => {
    setSpeakingSunday(!speakingSunday);
  };
  const heartFavoriteHandler = useCallback((e, speakerRec) => {
    e.preventDefault();
    const toggledRec = { ...speakerRec, favorite: !speakerRec.favorite };
    axios.put(`http://172.16.0.0:4000/speakers/${speakerRec.id}`, toggledRec)
      .then(function (response) {
        updateDataRecord(toggledRec);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  const newSpeakerList = useMemo(
    () =>
      data
        .filter(
          ({ sat, sun }) => (speakingSaturday && sat) || (speakingSunday && sun)
        )
        .sort(function (a, b) {
          if (a.firstName < b.firstName) {
            return -1;
          }
          if (a.firstName > b.firstName) {
            return 1;
          }
          return 0;
        }),
    [speakingSaturday, speakingSunday, data]
  );

  const speakerListFiltered = isLoading ? [] : newSpeakerList;

  if (hasErrored)
    return (
      <div>
        {errorMessage}&nbsp;"Make sure you have launched "npm run json-server"
      </div>
    );

  /*
  const [speakingSaturday, setSpeakingSaturday] = useState(true);
  const [speakingSunday, setSpeakingSunday] = useState(true);

  //const [isLoading, setIsLoading] = useState(true);
  //const [speakerList, setSpeakerList] = useState([]);
  //const [speakerList, setSpeakerList] = useReducer((state, action) => action, []);


  // function speakerReducer(state, action) {
  //   function updateFavorite(favoriteValue) {
  //     return state.map((item, index) => {
  //       if (item.id === action.sessionId) {
  //         item.favorite = favoriteValue;
  //         return item;
  //       }
  //       return item;
  //     });
  //   }
  //   switch (action.type) {
  //     case "setSpeakerList": {
  //       return action.data
  //     }
  //     case "favorite": {
  //       return updateFavorite(true);
  //     }
  //     case "unfavorite": {
  //       return updateFavorite(false);
  //     }
  //     default:
  //       return state;
  //   }
  // }

  //const [speakerList, dispatch] = useReducer(speakersReducer, []);

  const context = useContext(ConfigContext);

  // useEffect(() => {
  //   setIsLoading(true);
  //   new Promise(function (resolve) {
  //     setTimeout(function () {
  //       resolve();
  //     }, 1000);
  //   }).then(() => {
  //     setIsLoading(false);
  //     const speakerListServerFilter = SpeakerData.filter(({ sat, sun }) => {
  //       return (speakingSaturday && sat) || (speakingSunday && sun);
  //     });
  //     //setSpeakerList(speakerListServerFilter);
  //     dispatch({
  //       type: "setSpeakerList",
  //       data: speakerListServerFilter
  //     });
  //   });
  //   return () => {
  //     console.log("cleanup");
  //   };
  // }, []); // [speakingSunday, speakingSaturday]);


  const handleChangeSaturday = () => {
    setSpeakingSaturday(!speakingSaturday);
  };

  const handleChangeSunday = () => {
    setSpeakingSunday(!speakingSunday);
  };

  const heartFavoriteHandler = useCallback((e, favoriteValue) => {
    e.preventDefault();
    const sessionId = parseInt(e.target.attributes["data-sessionid"].value);
    dispatch({
      type: favoriteValue === true ? "favorite" : "unfavorite",
      sessionId
    });
    //console.log("changing session favorte to " + favoriteValue);
  }, []);

  const newSpeakerList = useMemo(
    () => data
      .filter(
        ({ sat, sun }) => (speakingSaturday && sat) || (speakingSunday && sun)
      )
      .sort(function (a, b) {
        if (a.firstName < b.firstName) {
          return -1;
        }
        if (a.firstName > b.firstName) {
          return 1;
        }
        return 0;
      }), [speakingSaturday, speakingSunday, data]);

  const speakerListFiltered = isLoading
    ? []
    : newSpeakerList;
  */

  if (isLoading) return <div>Loading...</div>;

  return (
    <div>
      <Header />
      <Menu />
      <div className="container">
        <div className="btn-toolbar  margintopbottom5 checkbox-bigger">
          <div className="hide">
            <div className="form-check-inline">
              <label className="form-check-label">
                <input
                  type="checkbox"
                  className="form-check-input"
                  onChange={handleChangeSaturday}
                  checked={speakingSaturday}
                />
                Saturday Speakers
              </label>
            </div>
            <div className="form-check-inline">
              <label className="form-check-label">
                <input
                  type="checkbox"
                  className="form-check-input"
                  onChange={handleChangeSunday}
                  checked={speakingSunday}
                />
                Sunday Speakers
              </label>
            </div>
          </div>
        </div>
        <div className="row">
          <div className="card-deck">
            {speakerListFiltered.map(
              ({ id, firstName, lastName, sat, sun, bio, favorite }) => {
                return (
                  <SpeakerDetail
                    key={id}
                    id={id}
                    favorite={favorite}
                    onHeartFavoriteHandler={heartFavoriteHandler}
                    firstName={firstName}
                    lastName={lastName}
                    bio={bio}
                    sat={sat}
                    sun={sun}
                  />
                );
              }
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Speakers;
