import React, { useState, useEffect, useContext } from "react";
//import "react-toastify/dist/ReactToastify.css";
import { ToastContainer, toast } from "react-toastify";
import { ConfigContext } from "./App";
import { inspect } from 'util';

const SignMeUp = ({ signupCallback }) => {
  useEffect(() => {
    console.log(`SignMeUp:useEffect called`);
  });

  const [email, setEmail] = useState();
  const [emailValid, setEmailValid] = useState(false);
  const [sendProcessing, setSendProcessing] = useState(false);

  const context = useContext(ConfigContext);

  function validateEmail(email) {
    const re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(email);
  }

  const notify = () => {
    toast.info(`You will be notified of upcoming events ${email}`);
  };

  function sendEmailToBackend() {
    setSendProcessing(true);
    new Promise(function (resolve) {
      setTimeout(function () {
        setSendProcessing(false);
        setEmail("");
        resolve();
      }, 1000);
    }).then(() => {
      notify();
      signupCallback(email);
      setEmail("");
    });
  }

  const buttonText = sendProcessing ? "processing..." : "Get Updates";

  //console.log('src/SignMeUp ConfigContext=%s', JSON.stringify(ConfigContext, null, 2))
  //console.log('\nsrc/SignMeUp ConfigContext=%s', inspect(ConfigContext));
  //console.log("src/SignMeUp ConfigContext.showSignMeUp=%s", ConfigContext.showSignMeUp);

  if (context.loggedInUserEmail) {
    return (
      <div className="container">
        <div className="content">
          <span>Logged in User Email: {context.loggedInUserEmail}</span>&nbsp;&nbsp;&nbsp;
          <a href='/logout'>Logout</a>
        </div>
      </div>
    );

  }

  return context.showSignMeUp === false ? null : (
    <div className="container">
      <div>
        <ToastContainer />
        <div className="content">
          <input
            value={email}
            onChange={e => {
              setEmailValid(validateEmail(e.target.value));
              return setEmail(e.target.value);
            }}
            placeholder="Enter Email"
            type="email"
            name="email"
            required
            required
          />
          &nbsp;
          <button
            disabled={!emailValid || sendProcessing}
            className="btn"
            onClick={sendEmailToBackend}
            type="submit"
          >
            {buttonText}
          </button>
          &nbsp;&nbsp;<a href="login">Login</a>
        </div>
      </div>
    </div>
  );
};

export default SignMeUp;
